import { useState, useEffect } from "react";
import axios from "axios";

function AddProductPage() {
  const [form, setForm] = useState({
    name: "",
    description: "",
    status: true,
    stock: "",
    price: "",
    category: "electronics",
    image: "",
  });

  const [images, setImages] = useState([]);

  useEffect(() => {
    // Fetch placeholder images from an API
    axios
      .get("https://api.unsplash.com/photos?client_id=YOUR_UNSPLASH_ACCESS_KEY&per_page=8")
      .then((res) => setImages(res.data))
      .catch((err) => console.error("Image fetch failed", err));
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/api/products", form)
      .then(() => alert("Product added!"))
      .catch((err) => alert("Error adding product"));
  };

  return (
    <div className="bg-slate-900 min-h-screen text-white p-8">
      <h1 className="text-3xl font-bold mb-8">Add New Product</h1>

      <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-6 max-w-2xl">
        {/* Name */}
        <div>
          <label className="block mb-1">Product Name</label>
          <input
            name="name"
            value={form.name}
            onChange={handleChange}
            className="w-full p-2 rounded bg-slate-800 border border-slate-700"
            placeholder="Enter product name"
            required
          />
        </div>

        {/* Description */}
        <div>
          <label className="block mb-1">Description</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            className="w-full p-2 rounded bg-slate-800 border border-slate-700"
            placeholder="Short description"
            required
          />
        </div>

        {/* Status */}
        <div>
          <label className="block mb-1">Status</label>
          <input
            type="checkbox"
            name="status"
            checked={form.status}
            onChange={handleChange}
            className="mr-2"
          />
          Active
        </div>

        {/* Category Dropdown */}
        <div>
          <label className="block mb-1">Category</label>
          <select
            name="category"
            value={form.category}
            onChange={handleChange}
            className="w-full p-2 rounded bg-slate-800 border border-slate-700"
          >
            <option value="electronics">Electronics</option>
            <option value="fashion">Fashion</option>
            <option value="beauty">Beauty</option>
            <option value="home">Home</option>
            <option value="gadgets">Gadgets</option>
          </select>
        </div>

        {/* Media - Choose Image */}
        <div>
          <label className="block mb-2">Choose an Image</label>
          <div className="grid grid-cols-4 gap-2">
            {Array.isArray(images) &&
              images.map((img) => (
                <img
                  key={img.id}
                  src={img.urls.thumb}
                  alt="product"
                  onClick={() => setForm({ ...form, image: img.urls.small })}
                  className={`w-full h-24 object-cover rounded cursor-pointer border-2 ${
                    form.image === img.urls.small ? "border-blue-500" : "border-transparent"
                  }`}
                />
              ))}
          </div>
        </div>

        {/* Stock */}
        <div>
          <label className="block mb-1">Stock</label>
          <input
            name="stock"
            type="number"
            value={form.stock}
            onChange={handleChange}
            className="w-full p-2 rounded bg-slate-800 border border-slate-700"
            placeholder="Quantity in stock"
            required
          />
        </div>

        {/* Price */}
        <div>
          <label className="block mb-1">Price ($)</label>
          <input
            name="price"
            type="number"
            value={form.price}
            onChange={handleChange}
            className="w-full p-2 rounded bg-slate-800 border border-slate-700"
            placeholder="Enter price"
            required
          />
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded"
        >
          Add Product
        </button>
      </form>
    </div>
  );
}

export default AddProductPage;
