import { useState } from 'react';
import axios from 'axios';

function AddProductPage() {
  const [product, setProduct] = useState({
    name: '',
    description: '',
    status: true,
    stock: '',
    price: '',
    category: '',
    image: '',
  });

const [searchTerm, setSearchTerm] = useState('');
const [imageResults, setImageResults] = useState([]);

const fetchImages = async () => {
  const res = await axios.get(
    `https://api.unsplash.com/search/photos`,
    {
      params: { query: searchTerm, per_page: 6 },
      headers: {
        Authorization: `Client-ID YOUR_UNSPLASH_ACCESS_KEY`
      }
    }
  );
  setImageResults(res.data.results);
};


  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setProduct({
      ...product,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/products', product);
    alert('Product added successfully!');
  };

  return (
    <div className="bg-slate-900 text-white min-h-screen p-6 mt-16">
      <div className="max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Left: Main product form */}
        <div className="lg:col-span-2 space-y-6">
          {/* Title and Description */}
          <div className="bg-slate-800 p-6 rounded shadow">
            <label className="block text-sm font-medium mb-1">Title</label>
            <input
              name="name"
              value={product.name}
              onChange={handleChange}
              className="w-full p-2 rounded bg-slate-700 text-white"
              placeholder="Short sleeve t-shirt"
            />

            <label className="block text-sm font-medium mt-4 mb-1">Description</label>
            <textarea
              name="description"
              value={product.description}
              onChange={handleChange}
              className="w-full p-2 rounded bg-slate-700 text-white min-h-[150px]"
              placeholder="Product description..."
            />
          </div>

          {/* Media Upload */}
          {/* Inside Media Section */}
<label className="block text-sm font-medium mb-2">Search for Product Image</label>
<div className="flex gap-2">
  <input
    type="text"
    className="p-2 rounded bg-slate-700 text-white w-full"
    placeholder="Search e.g. shoes, headphones"
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
  />
  <button
    onClick={fetchImages}
    type="button"
    className="bg-blue-600 px-4 rounded text-white"
  >
    Search
  </button>
</div>

{/* Show Image Options */}
<div className="grid grid-cols-3 gap-3 mt-4">
  {imageResults.map((img) => (
    <img
      key={img.id}
      src={img.urls.small}
      alt={img.alt_description}
      className={`rounded border-2 cursor-pointer hover:border-blue-500 ${
        product.image === img.urls.small ? 'border-blue-600' : 'border-slate-700'
      }`}
      onClick={() => setProduct({ ...product, image: img.urls.small })}
    />
  ))}
</div>

{/* Selected Image Preview */}
{product.image && (
  <div className="mt-4">
    <label className="block mb-1 text-sm">Selected Image</label>
    <img src={product.image} className="w-32 rounded" alt="Selected" />
  </div>
)}


          {/* Category, Price, Stock */}
          <div className="bg-slate-800 p-6 rounded shadow grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium mb-1">Category</label>
              <input
                name="category"
                value={product.category}
                onChange={handleChange}
                className="w-full p-2 rounded bg-slate-700 text-white"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Price</label>
              <input
                name="price"
                value={product.price}
                onChange={handleChange}
                type="number"
                className="w-full p-2 rounded bg-slate-700 text-white"
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-1">Stock</label>
              <input
                name="stock"
                value={product.stock}
                onChange={handleChange}
                type="number"
                className="w-full p-2 rounded bg-slate-700 text-white"
              />
            </div>
          </div>
        </div>

        {/* Right: Status and Meta */}
        <div className="space-y-6">
          <div className="bg-slate-800 p-6 rounded shadow">
            <label className="block text-sm font-medium mb-2">Status</label>
            <select
              name="status"
              value={product.status}
              onChange={handleChange}
              className="w-full p-2 rounded bg-slate-700 text-white"
            >
              <option value={true}>Active</option>
              <option value={false}>Draft</option>
            </select>
          </div>

          <div className="bg-slate-800 p-6 rounded shadow">
            <h4 className="font-medium text-base mb-2">Publishing</h4>
            <div className="text-slate-300 text-sm">
              <p>Sales Channels:</p>
              <ul className="ml-4 list-disc text-xs mt-1">
                <li>Online Store</li>
                <li>Point of Sale (optional)</li>
              </ul>
              <p className="mt-2">Markets: India and International</p>
            </div>
          </div>

          <div className="bg-slate-800 p-6 rounded shadow">
            <h4 className="font-medium text-base mb-2">Product Organization</h4>
            <input
              placeholder="Type"
              className="w-full p-2 mb-2 rounded bg-slate-700 text-white"
            />
            <input
              placeholder="Vendor"
              className="w-full p-2 mb-2 rounded bg-slate-700 text-white"
            />
            <input
              placeholder="Collections"
              className="w-full p-2 rounded bg-slate-700 text-white"
            />
          </div>

          <button
            onClick={handleSubmit}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded font-medium"
          >
            Save Product
          </button>
        </div>
      </div>
    </div>
  );
}

export default AddProductPage;
