import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useParams, Link, useNavigate } from 'react-router-dom';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [product, setProduct] = useState(null);
  const [selectedImage, setSelectedImage] = useState(0);
  const [quantity, setQuantity] = useState(1);

  // Simulated product data
  useEffect(() => {
    // Simulate API call to fetch product details
    setTimeout(() => {
      const productData = {
        id: parseInt(id),
        name: 'Premium Wireless Headphones',
        description: 'Experience crystal-clear sound with our premium wireless headphones. Featuring active noise cancellation, 40-hour battery life, and premium memory foam ear cushions for all-day comfort.',
        price: 149.99,
        stock: 24,
        sku: 'WH-PREM-2025',
        categories: ['Audio', 'Electronics', 'Accessories'],
        specifications: [
          { name: 'Battery Life', value: '40 hours' },
          { name: 'Connectivity', value: 'Bluetooth 5.2' },
          { name: 'Noise Cancellation', value: 'Active (ANC)' },
          { name: 'Driver Size', value: '40mm' },
          { name: 'Weight', value: '250g' }
        ],
        images: [
          '/api/placeholder/500/500',
          '/api/placeholder/500/500',
          '/api/placeholder/500/500'
        ],
        lastUpdated: '2025-03-15T14:30:00Z'
      };
      
      setProduct(productData);
      setIsLoading(false);
    }, 800);
  }, [id]);

  // Animations
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.6,
        when: "beforeChildren",
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.5 }
    }
  };

  const handleQuantityChange = (value) => {
    const newQuantity = Math.max(1, Math.min(product?.stock || 1, quantity + value));
    setQuantity(newQuantity);
  };

  const handleEdit = () => {
    navigate(`/products/edit/${id}`);
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return new Intl.DateTimeFormat('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    }).format(date);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-900 to-purple-800">
        <div className="text-white text-2xl">Loading product details...</div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-r from-blue-900 to-purple-800 text-white">
        <h2 className="text-3xl font-bold mb-4">Product Not Found</h2>
        <p className="mb-8">The product you're looking for doesn't exist or has been removed.</p>
        <Link to="/products" className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg transition duration-300">
          Back to Products
        </Link>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-900 to-purple-800 text-white pt-20">
      <div className="container mx-auto px-4 py-12">
        {/* Breadcrumb */}
        <div className="mb-8">
          <nav className="flex" aria-label="Breadcrumb">
            <ol className="inline-flex items-center space-x-1 md:space-x-3">
              <li className="inline-flex items-center">
                <Link to="/" className="text-blue-300 hover:text-white transition duration-300">
                  Home
                </Link>
              </li>
              <li>
                <div className="flex items-center">
                  <span className="mx-2 text-gray-400">/</span>
                  <Link to="/products" className="text-blue-300 hover:text-white transition duration-300">
                    Products
                  </Link>
                </div>
              </li>
              <li aria-current="page">
                <div className="flex items-center">
                  <span className="mx-2 text-gray-400">/</span>
                  <span className="text-blue-100">{product.name}</span>
                </div>
              </li>
            </ol>
          </nav>
        </div>

        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className="max-w-6xl mx-auto"
        >
          <div className="bg-white bg-opacity-10 backdrop-filter backdrop-blur-lg rounded-2xl p-6 md:p-8 shadow-xl border border-white border-opacity-20">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
              {/* Left: Product Details */}
              <motion.div variants={itemVariants} className="flex flex-col justify-between">
                <div>
                  <div className="flex justify-between items-start mb-4">
                    <h1 className="text-3xl md:text-4xl font-bold">{product.name}</h1>
                    <div className="flex items-center">
                      <button
                        onClick={handleEdit}
                        className="bg-blue-600 hover:bg-blue-700 text-white p-2 rounded-lg transition duration-300"
                      >
                        <span className="text-sm">✏️ Edit</span>
                      </button>
                    </div>
                  </div>

                  <div className="mb-6">
                    <div className="flex items-center mb-4">
                      <div className={`px-3 py-1 rounded-full ${product.stock > 10 ? 'bg-green-500' : product.stock > 0 ? 'bg-yellow-500' : 'bg-red-500'} text-white text-sm font-medium`}>
                        {product.stock > 10 ? 'In Stock' : product.stock > 0 ? 'Low Stock' : 'Out of Stock'}
                      </div>
                      <div className="ml-4 text-blue-200">
                        SKU: {product.sku}
                      </div>
                    </div>

                    <div className="text-3xl font-bold mb-4">
                      ${product.price.toFixed(2)}
                    </div>

                    <p className="text-blue-100 mb-6">{product.description}</p>

                    <div className="mb-8">
                      <h3 className="text-lg font-medium mb-3">Categories</h3>
                      <div className="flex flex-wrap gap-2">
                        {product.categories.map((category, index) => (
                          <span 
                            key={index} 
                            className="px-3 py-1 bg-blue-800 bg-opacity-40 rounded-full text-sm"
                          >
                            {category}
                          </span>
                        ))}
                      </div>
                    </div>

                    <div className="mb-8">
                      <h3 className="text-lg font-medium mb-3">Specifications</h3>
                      <div className="grid grid-cols-2 gap-3">
                        {product.specifications.map((spec, index) => (
                          <div key={index} className="bg-black bg-opacity-20 p-3 rounded-lg">
                            <p className="text-blue-300 text-sm">{spec.name}</p>
                            <p className="font-medium">{spec.value}</p>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>

                <div className="mt-8">
                  <div className="flex flex-col md:flex-row justify-between gap-4 md:items-center">
                    <div className="flex items-center border border-blue-700 rounded-lg overflow-hidden">
                      <button 
                        onClick={() => handleQuantityChange(-1)}
                        className="bg-blue-700 text-white px-4 py-2 hover:bg-blue-800 transition duration-300"
                      >
                        -
                      </button>
                      <input 
                        type="number" 
                        value={quantity}
                        readOnly
                        className="bg-transparent text-center w-16 text-white border-none focus:outline-none"
                      />
                      <button 
                        onClick={() => handleQuantityChange(1)}
                        className="bg-blue-700 text-white px-4 py-2 hover:bg-blue-800 transition duration-300"
                      >
                        +
                      </button>
                    </div>

                    <div className="flex gap-3">
                      <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition duration-300 flex-1"
                      >
                        Update Stock
                      </motion.button>
                      <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        className="px-6 py-3 bg-gradient-to-r from-purple-600 to-blue-500 hover:from-purple-700 hover:to-blue-600 text-white rounded-lg transition duration-300 flex-1"
                      >
                        Generate Report
                      </motion.button>
                    </div>
                  </div>

                  <div className="mt-4 text-blue-300 text-sm">
                    Last updated: {formatDate(product.lastUpdated)}
                  </div>
                </div>
              </motion.div>

              {/* Right: Product Images */}
              <motion.div variants={itemVariants} className="flex flex-col">
                <div className="bg-black bg-opacity-20 rounded-xl p-2 mb-4 aspect-square">
                  <motion.img
                    key={selectedImage}
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 0.3 }}
                    src={product.images[selectedImage]}
                    alt={`${product.name} - Image ${selectedImage + 1}`}
                    className="w-full h-full object-contain rounded-lg"
                  />
                </div>

                <div className="flex justify-center gap-2">
                  {product.images.map((image, index) => (
                    <button
                      key={index}
                      onClick={() => setSelectedImage(index)}
                      className={`w-16 h-16 rounded-lg overflow-hidden border-2 transition duration-300 ${
                        selectedImage === index ? 'border-blue-400' : 'border-transparent'
                      }`}
                    >
                      <img 
                        src={image} 
                        alt={`${product.name} thumbnail ${index + 1}`}
                        className="w-full h-full object-cover"
                      />
                    </button>
                  ))}
                </div>

                <div className="mt-8 bg-black bg-opacity-20 rounded-xl p-6">
                  <h3 className="text-lg font-medium mb-3">Inventory Details</h3>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <p className="text-blue-300 text-sm">Current Stock</p>
                      <p className="text-2xl font-bold">{product.stock}</p>
                    </div>
                    <div>
                      <p className="text-blue-300 text-sm">Value</p>
                      <p className="text-2xl font-bold">${(product.price * product.stock).toFixed(2)}</p>
                    </div>
                    <div>
                      <p className="text-blue-300 text-sm">Min. Level</p>
                      <p className="text-xl font-medium">10</p>
                    </div>
                    <div>
                      <p className="text-blue-300 text-sm">Reorder Status</p>
                      <p className={`text-xl font-medium ${product.stock <= 10 ? 'text-yellow-400' : 'text-green-400'}`}>
                        {product.stock <= 10 ? 'Reorder Soon' : 'Good'}
                      </p>
                    </div>
                  </div>
                </div>
              </motion.div>
            </div>
          </div>

          {/* Related Products */}
          <motion.div
            variants={itemVariants}
            className="mt-12 bg-white bg-opacity-5 backdrop-filter backdrop-blur-sm rounded-2xl p-8"
          >
            <h2 className="text-2xl font-bold mb-6">Related Products</h2>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {[1, 2, 3, 4].map((item) => (
                <div key={item} className="bg-white bg-opacity-10 rounded-lg overflow-hidden">
                  <div className="aspect-square bg-gray-700">
                    <img 
                      src="/api/placeholder/200/200" 
                      alt={`Related product ${item}`}
                      className="w-full h-full object-cover" 
                    />
                  </div>
                  <div className="p-4">
                    <h3 className="font-medium mb-1">Related Product {item}</h3>
                    <p className="text-blue-300">${(product.price - 20 + (item * 10)).toFixed(2)}</p>
                  </div>
                </div>
              ))}
            </div>
          </motion.div>
        </motion.div>
      </div>
    </div>
  );
}

export default ProductDetail;
