import React, { useState } from "react";
import { motion } from "framer-motion";

const AddProduct = () => {
  const [form, setForm] = useState({
    title: "",
    description: "",
    media: null,
    status: "draft",
    category: "",
    price: "",
    stock: "",
  });

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "file" ? files[0] : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted:", form);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-900 to-purple-800 flex items-center justify-center p-6">
      <motion.form
        onSubmit={handleSubmit}
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="flex flex-col lg:flex-row gap-6 w-full max-w-6xl"
      >
        {/* Left Side */}
        <div className="flex-1 space-y-6">
          {/* Title */}
          <div className="bg-white bg-opacity-20 backdrop-blur-lg p-4 rounded-xl shadow-lg">
            <label className="block text-sm font-medium text-white mb-1">Title</label>
            <input
              type="text"
              name="title"
              value={form.title}
              onChange={handleChange}
              placeholder="Product title"
              className="w-full p-2 rounded bg-white bg-opacity-20 placeholder-white text-white focus:outline-none focus:ring focus:ring-blue-300"
            />
          </div>

          {/* Description */}
          <div className="bg-white bg-opacity-20 backdrop-blur-lg p-4 rounded-xl shadow-lg">
            <label className="block text-sm font-medium text-white mb-1">Description</label>
            <textarea
              name="description"
              value={form.description}
              onChange={handleChange}
              rows="5"
              placeholder="Write product description..."
              className="w-full p-2 rounded bg-white bg-opacity-20 placeholder-white text-white focus:outline-none focus:ring focus:ring-blue-300"
            ></textarea>
          </div>

          {/* Media */}
          <div className="bg-white bg-opacity-20 backdrop-blur-lg p-4 rounded-xl shadow-lg">
            <label className="block text-sm font-medium text-white mb-1">Media</label>
            <input
              type="file"
              name="media"
              onChange={handleChange}
              accept="image/*"
              className="w-full text-sm text-white"
            />
          </div>

          {/* Submit Button */}
          <motion.button
            whileHover={{ scale: 1.05 }}
            type="submit"
            className="w-full lg:w-auto bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-md transition"
          >
            Add Product
          </motion.button>
        </div>

        {/* Right Side */}
        <div className="w-full lg:w-80 space-y-4">
          {/* Status */}
          <div className="bg-white bg-opacity-20 backdrop-blur-lg p-4 rounded-xl shadow-lg">
            <label className="block text-sm font-medium text-white mb-1">Status</label>
            <select
              name="status"
              value={form.status}
              onChange={handleChange}
              className="w-full p-2 rounded bg-white bg-opacity-20 text-white focus:outline-none focus:ring focus:ring-blue-300"
            >
              <option value="draft">Draft</option>
              <option value="active">Active</option>
            </select>
          </div>

          {/* Category */}
          <div className="bg-white bg-opacity-20 backdrop-blur-lg p-4 rounded-xl shadow-lg">
            <label className="block text-sm font-medium text-white mb-1">Category</label>
            <select
              name="category"
              value={form.category}
              onChange={handleChange}
              className="w-full p-2 rounded bg-white bg-opacity-20 text-white focus:outline-none focus:ring focus:ring-blue-300"
            >
              <option value="">Choose a category</option>
              <option value="electronics">Electronics</option>
              <option value="fashion">Fashion</option>
              <option value="beauty">Beauty</option>
              <option value="home">Home</option>
              <option value="gadgets">Gadgets</option>
            </select>
          </div>

          {/* Price */}
          <div className="bg-white bg-opacity-20 backdrop-blur-lg p-4 rounded-xl shadow-lg">
            <label className="block text-sm font-medium text-white mb-1">Price</label>
            <input
              type="number"
              name="price"
              value={form.price}
              onChange={handleChange}
              placeholder="$0.00"
              className="w-full p-2 rounded bg-white bg-opacity-20 text-white placeholder-white focus:outline-none focus:ring focus:ring-blue-300"
            />
          </div>

          {/* Stock */}
          <div className="bg-white bg-opacity-20 backdrop-blur-lg p-4 rounded-xl shadow-lg">
            <label className="block text-sm font-medium text-white mb-1">Stock</label>
            <input
              type="number"
              name="stock"
              value={form.stock}
              onChange={handleChange}
              placeholder="Stock quantity"
              className="w-full p-2 rounded bg-white bg-opacity-20 text-white placeholder-white focus:outline-none focus:ring focus:ring-blue-300"
            />
          </div>
        </div>
      </motion.form>
    </div>
  );
};

export default AddProduct;
