import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/products/${id}`)
      .then(res => setProduct(res.data))
      .catch(err => console.error("Failed to fetch product:", err));
  }, [id]);

  if (!product) return <p className="text-white text-center mt-10">Loading...</p>;

  return (
    <div className="max-w-6xl mx-auto px-4 py-10 text-white bg-gray-900 rounded-lg shadow-lg">
      <div className="flex flex-col md:flex-row items-center gap-10">
        <div className="w-full md:w-1/2">
          <img
            src={product.image || "https://via.placeholder.com/500"}
            alt={product.name}
            className="w-full h-auto rounded-lg shadow-md"
          />
        </div>
        <div className="w-full md:w-1/2 space-y-4">
          <h2 className="text-3xl font-bold text-yellow-400">{product.name}</h2>
          <p className="text-lg"><span className="font-semibold">Description:</span> {product.description}</p>
          <p className="text-lg"><span className="font-semibold">Price:</span> ${product.price}</p>
          <p className="text-lg"><span className="font-semibold">Category:</span> {product.category}</p>
          {/* Optional: Add to Cart or Edit/Delete buttons here */}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
