import { useEffect, useState } from 'react';
import axios from 'axios';

function ProductPage() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', price: '', category: '' });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    axios.get('/api/products').then((res) => setProducts(res.data));
  }, []);

  const handleDelete = (id) => {
    axios.delete(`/api/products/${id}`, {
      headers: { Authorization: localStorage.getItem('token') },
    }).then(() => setProducts(products.filter((p) => p._id !== id)));
  };

  const handleEdit = (product) => {
    setEditingId(product._id);
    setForm(product);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const config = { headers: { Authorization: localStorage.getItem('token') } };
    if (editingId) {
      axios.put(`/api/products/${editingId}`, form, config).then((res) => {
        setProducts(products.map(p => p._id === editingId ? res.data : p));
        setEditingId(null);
        setForm({ name: '', description: '', price: '', category: '' });
      });
    } else {
      axios.post('/api/products', form, config).then((res) => {
        setProducts([...products, res.data]);
        setForm({ name: '', description: '', price: '', category: '' });
      });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-slate-800 to-slate-900 text-white py-10 px-4">
      <div className="max-w-4xl mx-auto">
        <h2 className="text-3xl font-bold mb-6 text-center">Product Management</h2>

        <ul className="space-y-4 mb-10">
          {products.map((product) => (
            <li key={product._id} className="bg-slate-700 rounded-lg p-4 shadow flex justify-between items-center">
              <div>
                <h3 className="font-semibold text-lg">{product.name}</h3>
                <p className="text-sm text-slate-300">{product.description}</p>
                <p className="text-sm">Price: ${product.price}</p>
                <p className="text-sm italic">Category: {product.category}</p>
              </div>
              <div className="space-x-2">
                <button
                  onClick={() => handleEdit(product)}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(product._id)}
                  className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>

        <div className="bg-slate-700 p-6 rounded-lg shadow-lg">
          <h2 className="text-2xl font-semibold mb-4">{editingId ? 'Edit Product' : 'Add New Product'}</h2>
          <form onSubmit={handleSubmit} className="grid gap-4">
            <input
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Name"
              required
              className="p-3 rounded bg-slate-600 placeholder-white text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              name="description"
              value={form.description}
              onChange={handleChange}
              placeholder="Description"
              required
              className="p-3 rounded bg-slate-600 placeholder-white text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              name="price"
              value={form.price}
              onChange={handleChange}
              type="number"
              placeholder="Price"
              required
              className="p-3 rounded bg-slate-600 placeholder-white text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              name="category"
              value={form.category}
              onChange={handleChange}
              placeholder="Category"
              required
              className="p-3 rounded bg-slate-600 placeholder-white text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <button
              type="submit"
              className="bg-green-600 hover:bg-green-700 text-white font-semibold py-2 rounded transition duration-300"
            >
              {editingId ? 'Update Product' : 'Add Product'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default ProductPage;
