import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

function Home() {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [featuredProducts, setFeaturedProducts] = useState([
    { id: 1, name: 'Wireless Headphones', price: '$149.99', stock: 24, image: '/api/placeholder/100/100' },
    { id: 2, name: 'Smart Watch', price: '$299.99', stock: 18, image: '/api/placeholder/100/100' },
    { id: 3, name: 'Bluetooth Speaker', price: '$79.99', stock: 32, image: '/api/placeholder/100/100' }
  ]);

  useEffect(() => {
    // Simulate loading time
    const timer = setTimeout(() => {
      const userData = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null;
      setUser(userData);
      setIsLoading(false);
    }, 800);
    
    
    return () => clearTimeout(timer);
  }, []);

  

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        duration: 0.8,
        when: "beforeChildren",
        staggerChildren: 0.3
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { 
      y: 0, 
      opacity: 1,
      transition: { duration: 0.5 }
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-900 to-purple-800">
        <div className="text-white text-2xl">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-900 to-purple-800 text-white pt-20">
      {/* Main Content */}
      <div className="container mx-auto px-4 py-12">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className="max-w-5xl mx-auto"
        >
          <motion.div 
            variants={itemVariants}
            className="text-center mb-16"
          >
            <h1 className="text-5xl font-bold mb-6">Welcome to ProductMaster</h1>
            <p className="text-xl text-blue-200 max-w-2xl mx-auto">
              Manage your product inventory with ease. Add, edit, and track all your products in one place.
            </p>
          </motion.div>

          {/* Main Product Actions */}
          <motion.div
            variants={itemVariants} 
            className="grid md:grid-cols-2 gap-6 mb-16"
          >
            <Link to="/products">
              <motion.div
                whileHover={{ y: -5, transition: { duration: 0.2 } }}
                className="bg-white bg-opacity-10 backdrop-filter backdrop-blur-lg rounded-xl p-8 shadow-lg border border-white border-opacity-20 h-full"
              >
                <div className="h-16 w-16 rounded-xl bg-blue-500 mb-6 flex items-center justify-center">
                  <span className="text-3xl">ðŸ“¦</span>
                </div>
                <h3 className="text-2xl font-bold mb-4">Browse Products</h3>
                <p className="text-blue-200 mb-6">
                  View your complete product catalog, filter by categories, and check inventory status.
                </p>
                <div className="flex justify-between items-center">
                  <span className="text-lg font-medium">View all products</span>
                  <span className="text-2xl">â†’</span>
                </div>
              </motion.div>
            </Link>

            <Link to="/add-product">
              <motion.div
                whileHover={{ y: -5, transition: { duration: 0.2 } }}
                className="bg-white bg-opacity-10 backdrop-filter backdrop-blur-lg rounded-xl p-8 shadow-lg border border-white border-opacity-20 h-full"
              >
                <div className="h-16 w-16 rounded-xl bg-green-500 mb-6 flex items-center justify-center">
                  <span className="text-3xl">âž•</span>
                </div>
                <h3 className="text-2xl font-bold mb-4">Add New Product</h3>
                <p className="text-blue-200 mb-6">
                  Add new products to your inventory with detailed information, pricing, and stock levels.
                </p>
                <div className="flex justify-between items-center">
                  <span className="text-lg font-medium">Create product</span>
                  <span className="text-2xl">â†’</span>
                </div>
              </motion.div>
            </Link>
          </motion.div>

          {/* Quick Stats Section */}
          {user && (
            <motion.div 
              variants={itemVariants}
              className="bg-black bg-opacity-30 rounded-2xl p-8 mb-16"
            >
              <h2 className="text-2xl font-bold mb-6">Your Inventory at a Glance</h2>

              <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                {[
                  { label: 'Total Products', value: '124' },
                  { label: 'Low Stock Items', value: '12' },
                  { label: 'Total Value', value: '$28,480' }
                ].map((stat, index) => (
                  <motion.div 
                    key={stat.label}
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: 0.6 + index * 0.2 }}
                    className="bg-gradient-to-br from-blue-700 to-blue-900 rounded-lg p-4"
                  >
                    <p className="text-blue-300 text-sm">{stat.label}</p>
                    <p className="text-2xl font-bold">{stat.value}</p>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          )}

          {/* Featured Products */}
          <motion.div 
            variants={itemVariants}
            className="bg-white bg-opacity-5 backdrop-filter backdrop-blur-sm rounded-2xl p-8 mb-16"
          >
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold">Featured Products</h2>
              <Link to="/products" className="text-blue-300 hover:text-white transition duration-300">
                View all â†’
              </Link>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {featuredProducts.map((product, index) => (
                <motion.div 
                  key={product.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.8 + index * 0.2 }}
                  whileHover={{ y: -5, transition: { duration: 0.2 } }}
                  className="bg-white bg-opacity-10 backdrop-filter backdrop-blur-sm rounded-xl overflow-hidden border border-white border-opacity-10"
                >
                  <div className="aspect-square bg-gray-700 flex items-center justify-center">
                    <img 
                      src={product.image} 
                      alt={product.name} 
                      className="w-full h-full object-cover"
                    />
                  </div>
                  <div className="p-4">
                    <h3 className="font-medium text-lg mb-1">{product.name}</h3>
                    <div className="flex justify-between items-center">
                      <span className="text-blue-200">{product.price}</span>
                      <span className={`text-sm ${product.stock > 20 ? 'text-green-400' : 'text-yellow-400'}`}>
                        Stock: {product.stock}
                      </span>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Recent Activity */}
          <motion.div 
            variants={itemVariants}
            className="bg-white bg-opacity-5 backdrop-filter backdrop-blur-sm rounded-2xl p-8"
          >
            <h2 className="text-2xl font-bold mb-6">Recent Activity</h2>

            <div className="space-y-4">
              {[
                { action: 'Added new product', product: 'Wireless Headphones', time: '2 hours ago' },
                { action: 'Updated inventory', product: 'Smart Watch', time: '4 hours ago' },
                { action: 'Price changed', product: 'Bluetooth Speaker', time: '1 day ago' }
              ].map((activity, index) => (
                <motion.div 
                  key={index}
                  initial={{ x: -20, opacity: 0 }}
                  animate={{ x: 0, opacity: 1 }}
                  transition={{ delay: 0.8 + index * 0.2 }}
                  className="flex items-center border-b border-blue-800 border-opacity-30 pb-4"
                >
                  <div className="h-10 w-10 rounded-full bg-blue-700 flex items-center justify-center mr-4">
                    {index === 0 ? 'âž•' : index === 1 ? 'ðŸ”„' : 'ðŸ’°'}
                  </div>
                  <div className="flex-1">
                    <p className="font-medium">{activity.action}</p>
                    <p className="text-blue-300 text-sm">{activity.product}</p>
                  </div>
                  <div className="text-blue-400 text-sm">{activity.time}</div>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </motion.div>
      </div>

      {/* Footer */}
      <footer className="bg-black bg-opacity-30 py-8 mt-16">
        <div className="container mx-auto px-4 text-center">
          <p className="text-blue-300">Â© 2025 ProductMaster. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}

export default Home;
