import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

function Home() {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time
    const timer = setTimeout(() => {
      const userData = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null;
      setUser(userData);
      setIsLoading(false);
    }, 1000);
    
    return () => clearTimeout(timer);
  }, []);

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        duration: 0.8,
        when: "beforeChildren",
        staggerChildren: 0.3
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { 
      y: 0, 
      opacity: 1,
      transition: { duration: 0.5 }
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-900 to-purple-800">
        <div className="text-white text-2xl">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-900 to-purple-800 text-white">
      {/* Navigation Bar */}
      <nav className="bg-black bg-opacity-30 p-4">
        <div className="container mx-auto flex justify-between items-center">
          <motion.div 
            initial={{ x: -50, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="text-2xl font-bold"
          >
            ProductMaster
          </motion.div>
          
          {user && (
            <motion.div 
              initial={{ x: 50, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.5 }}
              className="flex items-center space-x-4"
            >
              <div className="relative">
                <div className="h-10 w-10 rounded-full bg-purple-500 flex items-center justify-center text-lg font-bold">
                  {user.email?.charAt(0).toUpperCase()}
                </div>
                <div className="absolute -bottom-1 -right-1 h-3 w-3 rounded-full bg-green-400 border-2 border-purple-800"></div>
              </div>
              <div>
                <p className="text-sm font-light">Welcome back,</p>
                <p className="font-medium">{user.email}</p>
              </div>
            </motion.div>
          )}
        </div>
      </nav>

      {/* Main Content */}
      <div className="container mx-auto px-4 py-16">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className="max-w-4xl mx-auto"
        >
          <motion.div 
            variants={itemVariants}
            className="text-center mb-16"
          >
            <h1 className="text-5xl font-bold mb-6">Welcome to ProductMaster</h1>
            <p className="text-xl text-blue-200 max-w-2xl mx-auto">
              Your complete solution for managing products, inventory, and more. Streamline your workflows and boost productivity.
            </p>
          </motion.div>

          {/* Feature Cards */}
          <div className="grid md:grid-cols-3 gap-8 mb-16">
            {['Products', 'Analytics', 'Settings'].map((item, index) => (
              <motion.div
                key={item}
                variants={itemVariants}
                whileHover={{ y: -10, transition: { duration: 0.2 } }}
                className="bg-white bg-opacity-10 backdrop-filter backdrop-blur-lg rounded-xl p-6 shadow-lg border border-white border-opacity-20"
              >
                <div className="h-12 w-12 rounded-lg bg-blue-500 mb-4 flex items-center justify-center">
                  <span className="text-2xl">
                    {index === 0 ? '📦' : index === 1 ? '📊' : '⚙️'}
                  </span>
                </div>
                <h3 className="text-xl font-bold mb-2">{item}</h3>
                <p className="text-blue-200">
                  {index === 0 ? 'Manage your product catalog with ease.' : 
                   index === 1 ? 'Get insights into your business performance.' : 
                   'Configure your workspace preferences.'}
                </p>
              </motion.div>
            ))}
          </div>

          {/* Quick Stats Section */}
          {user && (
            <motion.div 
              variants={itemVariants}
              className="bg-black bg-opacity-30 rounded-2xl p-8 mb-16"
            >
              <h2 className="text-2xl font-bold mb-6">Your Dashboard Overview</h2>

              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {[
                  { label: 'Products', value: '124' },
                  { label: 'Sales', value: '$12,480' },
                  { label: 'Growth', value: '+24%' },
                  { label: 'Customers', value: '1,240' }
                ].map((stat, index) => (
                  <motion.div 
                    key={stat.label}
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: 0.6 + index * 0.2 }}
                    className="bg-gradient-to-br from-blue-700 to-blue-900 rounded-lg p-4"
                  >
                    <p className="text-blue-300 text-sm">{stat.label}</p>
                    <p className="text-2xl font-bold">{stat.value}</p>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          )}

          {/* Recent Activity */}
          <motion.div 
            variants={itemVariants}
            className="bg-white bg-opacity-5 backdrop-filter backdrop-blur-sm rounded-2xl p-8"
          >
            <h2 className="text-2xl font-bold mb-6">Recent Activity</h2>

            <div className="space-y-4">
              {[
                { action: 'Added new product', product: 'Wireless Headphones', time: '2 hours ago' },
                { action: 'Updated inventory', product: 'Smart Watch', time: '4 hours ago' },
                { action: 'Price changed', product: 'Bluetooth Speaker', time: '1 day ago' }
              ].map((activity, index) => (
                <motion.div 
                  key={index}
                  initial={{ x: -20, opacity: 0 }}
                  animate={{ x: 0, opacity: 1 }}
                  transition={{ delay: 0.8 + index * 0.2 }}
                  className="flex items-center border-b border-blue-800 border-opacity-30 pb-4"
                >
                  <div className="h-10 w-10 rounded-full bg-blue-700 flex items-center justify-center mr-4">
                    {index === 0 ? '➕' : index === 1 ? '🔄' : '💰'}
                  </div>
                  <div className="flex-1">
                    <p className="font-medium">{activity.action}</p>
                    <p className="text-blue-300 text-sm">{activity.product}</p>
                  </div>
                  <div className="text-blue-400 text-sm">{activity.time}</div>
                </motion.div>
              ))}
            </div>
          </motion.div>
        </motion.div>
      </div>

      {/* Footer */}
      <footer className="bg-black bg-opacity-30 py-8 mt-16">
        <div className="container mx-auto px-4 text-center">
          <p className="text-blue-300">© 2025 ProductMaster. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}

export default Home;
